insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (19,'王生','','13249495135','1.在东厦北路附二医院，机动车道隔离栏被破坏，对面商铺及行人直接冲过马路\n2.木兰园的东厦路门，有人安装了2个移动遮阳棚，每天在那摆摊，垃圾随意倒\n3.东厦路木兰园那个恒晟超市，在门前左右两边上安装石礅，堵住汽车停放，导致许多车辆只能停在路边','2016-05-21 12:17:45',0,1,'163.204.9.39',NULL,12);
