insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (23,'罗宗乐','upimg/2016052401442853489.jpg,upimg/2016052401444390186.jpg,upimg/2016052401445727512.jpg,','13750453211','位于龙湖区珠池街道珠池综合市场与珠南社区接壤一带摊贩占道经营现象十分突出，阻塞消防通道，甚至占领附近居民的出入口。请市城管局督促龙湖区相关部门给予整治。','2016-05-24 01:48:31',0,1,'120.239.9.162',NULL,12);
