insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (60,'颜','','13510633271','广厦路红茘园邻街铺面占用停车位到底谁来管？车主要停车铺面占道。停在没划线的地方交警要抄牌。这现象只有汕头才有。汕头再整治还这样。老百姓能服吗?我看当官的都可以回家享福了。','2016-05-29 20:53:11',0,1,'14.119.112.219',NULL,12);
