insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (100,'刘先生','','13502997126','位于汕头市龙湖区丹阳庄东区38幢西侧的发廊乱搭乱建，占用了金华苑小区路，而且又将不用的用品乱放置在小区内，脏乱差很严重，住户多次投诉未果！这次我市进行创文活动，我们金华苑小区全体业主坚决要求本小区不留卫生死角，也请政府部门进行清理，不给本次创文活动抹黑','2016-06-05 23:45:58',0,1,'125.91.17.180',NULL,12);
