insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (163,'林少华','','18923998389','天山路与长江路交界的汕头购书中心门口搭建舞台，釆用高音播放设备播放活动，现在接近中考，影响中考学子的学习，请及时予以查处，谢谢！','2016-06-11 18:46:52',0,1,'116.26.113.135',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (164,'李先生','upimg/2016061118522016190.jpeg,upimg/2016061118523065228.jpeg,upimg/2016061118523913073.jpeg,','18688007577','陵海路四巷这里垃圾堆，没人清理，请有关部门应该及时清理','2016-06-11 18:54:34',0,1,'125.91.22.107',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (165,'黄先生','upimg/2016061119392642695.jpg,upimg/2016061119401227742.jpg,upimg/2016061119402197695.jpg,','15815923132','自从前几天开始整顿永平路口市场之后，明显感觉到道路宽敞了多。然后路过时发觉这间人行步道上的小屋子不属于违建吗？','2016-06-11 19:44:19',0,1,'14.126.190.87',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (166,'谢庆生','','13612329250','在当前政府和市民全力创文的形势下，东墩市场对面路段一到晚上饮食摊档占道经营的现象依然非常突出，到处污水横流，垃圾遍地，给市民的出行造成不便，希望城管部门加大力度整治，还给市民一条整洁畅通的道路，共同为创建文明城市出一份力。','2016-06-11 22:37:06',0,1,'218.76.130.192',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (167,'杨','upimg/2016061122470473825.jpg,','13531279667','中山路42号楼梯口下水道堵塞污水横流，滋生蚊蝇，在目前全市创文的氛围下极其不协调…','2016-06-11 22:51:32',0,1,'61.164.243.131',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (168,'张培俊','','13553364172','中山公园旁(杏花桥下)不应设市场。应该引导到大华市场。或华坞市场。\n','2016-06-11 22:55:10',0,1,'42.123.92.113',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (169,'陈山','upimg/2016061208275648998.jpg,upimg/2016061208283521173.jpg,upimg/2016061208291386148.jpg,','17708232495','尊敬同志您们好！同益花园物业违规把一条原来出入方便公共道路非法霸占，私设保安亭和路障作为停车场对外收费，严重造成居民出入难，每天居民都要经过铁栏杆上市场可想而知，实际同益花园本身己有地下宽阔车场，现在全市正在创全国卫生城市，居民强烈城管同志在百忙之中抽出时间予以关注，指示有关部门给予清除还路于民改善环境。祝城管同志身体健康多为人民服务。谢谢！\n','2016-06-12 08:57:10',0,1,'218.16.176.4',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (170,'林先生','','13750405736','尊敬的城管工作人员您们好！\n我住东厦北路金墩园41幢，楼下原本是商业铺面，已经是铺面门口也就是说自家的门口很大！可是很多不法商户对住宅区私自对外墙进行破坏修建后门，影响城市外观形象也造成该楼存在安全隐患。该楼上住宅居民对此十分气愤，对住宅居民造成不便！希望市城管部门给以重视和监管！\n                                                  敬礼！','2016-06-12 12:08:30',0,1,'61.164.243.130',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (171,'林伟豪','upimg/2016061212002850440.jpg,upimg/2016061212100025968.jpg,upimg/2016061212111990557.jpg,upimg/2016061212122466164.jpg,upimg/2016061212133275890.jpg,upimg/2016061212143857510.jpg,','13729292953','与市政府一墙之隔的外马路50号楼下，有一下水道因破损和很久没有清淤。每次下大雨就会把下水道的淤积物激上路面臭不可闻，也给居民出行造成影响。希城管部门能及早解决这一困扰多年的问题。','2016-06-12 12:15:09',0,1,'119.121.177.171',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (172,'过路人','','13411569895','黄山路珠池路金丰花园围蔽占用大部分人行步道封闭施工几年，内部已基本建成围蔽拆除几个月，由于施工的破坏原步道已完全不让人通行了，行人只能在机动车道与车辆抢走，加之该路段逆行的摩托車很多，存在很大的安全隐患！希望催促施工方尽快恢复人行通道，还道于民。今后，如此类占道建筑建议责成先恢復占道再完善内部建設。','2016-06-12 13:05:57',0,1,'14.158.216.137',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (173,'文静','','13723203134','石榴园38幢701，已经三天没上楼收垃圾','2016-06-12 13:19:15',0,1,'106.5.196.13',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (174,'郑世彬','','15816676360','为什么广兴街东2巷，东3巷没人来整理，现在大马路不准给人摆摊了，全部一早就到巷头巷尾来摆，把整条街道都摆满了，人，车根本就过不去。希望能得到关注','2016-06-12 15:34:03',0,1,'61.164.250.71',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (175,'随风','upimg/2016061217343062786.jpeg,upimg/2016061217350547672.jpeg,upimg/2016061217352468809.MOV,','15113116605','位于金平区飞夏老邮电局附近，长期在此，无人来管，是否合适？','2016-06-12 17:37:16',0,1,'14.215.33.126',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (176,'王宝华','upimg/2016061219223333996.jpeg,upimg/2016061219224536908.jpeg,upimg/2016061219230022288.jpeg,','13428302986','汕头市石榴园违法搭建严重，几乎每栋住宅楼的一层都是占地违章建筑，希望有关部门在这次创文行动中下大力气淸除违章建筑物，还市民一个优雅的生活环境。','2016-06-12 19:26:16',0,1,'119.121.108.115',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (177,'刘先生','upimg/2016061220153781224.jpeg,upimg/2016061220163198297.jpeg,upimg/2016061220174374909.jpeg,','13502997126','位于龙湖区东区38幢西侧的特斯美发廊后面，十年多来长期占用公共地方，私搭乱建，多次投诉无门，详见上传图片。当前我市正在进行创文建设，如此脏乱差的现象存在有关部门应坚决取缔！还路于民！','2016-06-12 20:17:55',0,1,'116.31.128.15',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (178,'小纪','','13049060490','第二期曝光广东省汕头市龙湖区陈厝合安和街限期（6月05日17时前）拆除的升降机依然耸立存在，还存在超高建设、未报建手续与出售小产权房各种违法行为，到底是业主太过强势还是执法部门执法不到位，多次下达法文都无起作用！','2016-06-12 22:11:40',0,1,'27.41.181.88',NULL,12);
