insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (184,'虹','','13715985709','为什么新疆人摆烧烤摊你们城管不敢去取缔，老是去处理小生意的广告牌。明摆占路你们不弄？','2016-06-13 13:54:18',0,1,'14.215.32.110',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (185,'林佳频','upimg/2016061314034841621.jpg,','13502996589','市区夏桂浦群星小学路段，废土堆积，下雨天学生上学、家长接送苦不堪言。请清理废土、平整路面。','2016-06-13 14:06:46',0,1,'61.164.250.68',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (186,'李先生','upimg/2016061315503245845.jpeg,upimg/2016061315511983273.jpeg,upimg/2016061315513622488.jpeg,upimg/2016061315515039707.jpeg,','18688007577','陵海路5巷，僵尸车长期停放，垃圾成堆。希望有关部门重视，及时清理','2016-06-13 15:54:15',0,1,'183.240.197.139',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (187,'吴先生','upimg/2016061317321416314.jpeg,upimg/2016061317324684374.jpeg,','13676103100','该规章建筑位于永泰路141号。永泰路做为城市的主要出入口，该铁皮屋长期占用步道，当前全市正在高举创文行动，如果该违规建筑不拆除，未免和当前的创文行动不协调。','2016-06-13 17:37:27',0,1,'14.215.34.44',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (188,'陈','upimg/2016061323103572597.MOV,','13531283251','全汕头人民都在为创建文明汕头而努力，而在汕头的光华北二路这里有这么一境，实在有煞风景','2016-06-13 23:15:09',0,1,'120.82.120.11',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (189,'张','','13421863214','说到文明，有能力的话来潮南区处啊！所谓的城管只不过是有钱就处理了，没钱就不管不问的呗','2016-06-14 00:36:55',0,1,'106.5.196.13',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (190,'陈小姐','','13113487823','今天龙眼南路开始拆除违章建筑，也请执法人员往园区里走，其实真正的违建是在园区里，明明是很宽敞的区间路，硬是给某些业主围成了自家住宅，明明同时可以并排3辆小车的区间路硬是给弄成了1辆小车还难以通行，要再加上某些车主乱停车，那在小区里的车就连小区都出不去了，如果不幸发生火灾，那消防车只能是插上翅膀进来了，请执法人员往里走一走，将真正危害公共安全的违章建筑清除，还我们一个安全的生活环境。','2016-06-14 09:35:45',0,1,'112.97.56.129',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (191,'林先生','upimg/2016061409351593336.jpg,','13556456982','照片中是，金平区光华路126号小区，消防通道被商铺占用作为，仓库使用，消防通道是生命通道，居民意见纷纷，小区商铺乱搭乱建非常突出。','2016-06-14 09:47:36',0,1,'14.215.60.239',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (192,'徐胜','upimg/2016061410224345733.png,','18688000322','请问店铺招牌广告的标准还没有出台，怎么都拆了，这样会造成民怨的。','2016-06-14 10:22:45',0,1,'59.35.107.158',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (193,'陈山','upimg/2016061410114690359.jpg,upimg/2016061410121841368.jpg,upimg/2016061410125213565.jpg,upimg/2016061410133025573.jpg,','17708232495','您们好！城管同志辛苦，我代表市民向您们道一声谢谢！我举报地址是同益花园与利安路交界这条路同益物业违规塔建保安亭收费搞创收，路内所有通往同益市场的路用铁栏杆载住，居民出入难，强烈要求政府把铁栏杆清除掉还路于民，其实同益有地下停车场。汕头在创文活动中，城管是主力军是好样的，谢谢','2016-06-14 10:27:16',0,1,'219.128.158.52',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (194,'纪生','upimg/2016061412314482833.jpg,','13068913408','根据巜汕头经济特区农村村民住宅建设管理办法》；汕头市龙湖区新津街道南碧埠社区桂和街11，13号无视政府的政策法规，存在违规违法行为，现己建设有十多层小产权房准备岀售，目前还在继续建设中，希望有关部门加强管理。','2016-06-14 12:31:52',0,1,'125.91.82.169',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (195,'林居','','13809653574','新厦路及兴业路的路灯长期不亮，盼修复！','2016-06-14 13:07:14',0,1,'106.5.196.13',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (196,'李姐','upimg/2016061415222572575.jpeg,upimg/2016061415223334348.jpeg,','15914776823','图为市金平区卫工路一横4号农村合作社门口。在全民上下齐心协力支持创文工作之际，个别市民或商户私自占用公共步道自行安装停车锁，有损城市文明发展，给市民日常生活带来不便，望有关部门予以高度重视，早日还路于民！！！','2016-06-14 15:23:22',0,1,'14.215.37.47',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (197,'吴佳銮','','13267771735','金园路与龙眼路交界处有新疆人摆摊卖哈密瓜！！！有本事来抓！','2016-06-14 18:06:51',0,1,'59.35.50.112',NULL,12);
