insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (220,'吴','upimg/2016061612063848158.jpeg,upimg/2016061612065899483.jpeg,','13923667288','位于公信路罗长发超市与外贸幼儿园中间这里有一间违章搭建的铁皮屋。请城管给予管一管。','2016-06-16 12:08:21',0,1,'112.97.56.49',NULL,12);
