insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (244,'好心人','','13192302573','汕头全市创文是好事，市民人人得拥护，但龙湖区龙祥街道夏桂埔居委区域仍然存在脏乱差情况：1，浦江路车辆乱停乱放仍然突出。2，夏桂埔嵩山路段西面与衡山路中间区域高压线下面存在大量违章搭建棚户区厂房，安全隐患严重。3，夏桂埔居委金桂园至国家乒乓球训练基地仍然出现大量棚户区及违章厂房','2016-06-17 19:31:58',0,1,'36.111.128.209',NULL,12);
