insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (281,'马馥珣','upimg/2016062020463595369.jpg,upimg/2016062020465745422.jpg,','13433872145','虽然图片暗了些，但我想说：这是占用安全通道，给消防救援造成困难，城管部门要严肃处理。','2016-06-20 20:48:56',0,1,'14.215.37.47',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (282,'创文','','13556387256','媒体多次爆光的火车站广场乱象，在一月前再次爆光以來，我每天几乎都到那里散步，其间仅见到一次市绿化中心到场宣传和干涉过一次，至今乱象依旧。不知是否己经默許？目前创文行动正在全市轰轰烈烈进行，本人认为此处的整治也不能拖后腿，对直接参与者要制止，对给大型充气城堡等提供电源的给予罚款直至停供。','2016-06-20 21:13:07',0,1,'14.158.221.219',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (283,'陈川页','','13670526485','汕头市大北山路34号宜悦雅轩，福春堂等铺户占用人行步道，置使每晚道路停放很多汽车，造成附近居民出行不便，希望汕头城管，管一管，还路于民。','2016-06-21 00:13:05',0,1,'106.5.196.13',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (284,'林林','upimg/2016062107553586585.jpeg,','13417127337','金砂西路4号，墙外围自己搭建的，违章的，把路变得很小，消防车进不了，经常车祸。','2016-06-21 07:58:17',0,1,'157.122.148.160',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (285,'郑先生','upimg/2016062111281497464.jpg,upimg/2016062111314041681.jpg,upimg/2016062111355216245.jpg,','13172547562','金平区月季市场牛皮癣型的违章搭建！久疾！顽疾！卫生问题极其严重，希望还居民一个卫生的居住环境！','2016-06-21 11:36:01',0,1,'106.5.196.13',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (286,'陈','upimg/2016062111402111560.jpg,','13016653850','珠峰路春兰园大门口有一路灯杆倾斜多日无人理，你理不？','2016-06-21 11:42:28',0,1,'113.106.170.142',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (287,'沈长江','','13501401454','市区大南山路原设有一市场，方便长江公寓及周边市民购物。后来，市场业主却将场内一半的面积租给物流公司使用。每日货车络绎不绝，时常将市场门口挤得水泄不通，工人不分昼夜装卸货物，噪音扰民，鸡犬不宁，商户怨气冲天，住户叫苦连天。让众人更为诟病的是，物流方居然在外围搭建大铁硼，虽多次投诉均未果。现急切希望借“创文”此股正气之风，吹散这乌烟瘴气，还民于市，恢复原貌。','2016-06-21 12:03:31',0,1,'183.46.15.72',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (288,'陈鹏','upimg/2016062116364055975.jpg,upimg/2016062116364944716.jpg,','13433347890','大学路197号好旺夜粥，违法占用居民通道搭设厨房，多次投诉未果。','2016-06-21 16:38:55',0,1,'125.91.18.218',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (289,'哥','upimg/2016062117101859773.jpeg,upimg/2016062117103964225.jpeg,upimg/2016062117105863099.jpeg,upimg/2016062117111075989.jpeg,','13631000321','垃圾堆以前很干净，现在一天比一天脏。晚上垃圾堆老鼠几十只。老鼠会掏空建筑地基。。还有农村建10层高没人管吗。不是规定不超过3层吗','2016-06-21 17:13:48',0,1,'183.7.111.171',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (290,'陈兄','upimg/2016062117232663407.jpg,upimg/2016062117233742019.jpg,','18023210897','东方街道竹园片区长期存在底层住户占用公共用地等违章搭建物现象，请有关部门落实整治，还周边居民通畅应急通道和生活环境。','2016-06-21 17:23:54',0,1,'183.41.36.229',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (291,'李先生','upimg/2016062117594899060.jpeg,upimg/2016062118003384956.jpeg,upimg/2016062118031720318.jpeg,upimg/2016062118032743475.jpeg,','18688007577','金平区三让路一横，公共停车位被人为的变成收费停车场，而且该停车车占用步道，违章建筑铁皮屋，停车蓬，希望有关部门给予取缔，还路于民。此致','2016-06-21 18:03:43',0,1,'183.240.197.138',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (292,'姚伟滨','upimg/2016062118231044134.jpg,','18688007492','玫瑰园占用公共地方的违建，何时可以还哭路于民','2016-06-21 18:23:54',0,1,'221.5.20.152',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (293,'新思路','upimg/2016062122245445216.jpg,upimg/2016062122262248073.jpg,upimg/2016062122274769147.jpg,','13502954325','一副围墙真趣味，\n占道圈地已多年。\n本来小巷经常堵，\n空间能否还厝边？\n金新路123号锅炉厂宿舍区，占道围墙望能拆除','2016-06-21 22:27:56',0,1,'61.164.250.68',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (294,'李宏明','','13715999411','同益路为了路边创文放石墩…施工单位直接把整条路的每栋楼的下水道直接赌死？整个同益路德居民下水道都被水泥封死，居民厕所全部都不能用…','2016-06-21 22:49:25',0,1,'121.11.10.219',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (295,'郭奕洲','','13415092496','汕头龙眼市场夜宵档死灰复燃，现在是北京时间2016.6.21号，夜宵档都出来了，希望有关部门可以监管到位，还路于民，谢谢','2016-06-21 23:36:09',0,1,'14.215.59.135',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (296,'孙','','13750480722','在长平路长平手机城前面，每到晚上十点后，就会有夜宵摊贩乱摆摊设点，营业时间直到隔天早上4、5点，不仅对周边环境造成严重污染，也对周围居民造成巨大的噪音和空气污染！在创文行动如此高涨的情况下还有如此占道扰民的问题出现，望城管部门允予管治，还周围居民一个良好的生活环境！鉴于对自身的保护，不便上传照片！','2016-06-21 23:36:28',0,1,'61.164.243.129',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (297,'郭奕洲','upimg/2016062123445187107.MOV,upimg/2016062123452528663.MOV,upimg/2016062123460510648.MOV,','13415092496','北京时间2616年6月21号，23:47分，中山路金凤坛附近夜宵档死灰复燃，希望有关部门可以严抓，还路于民，而不是三分钟热度，谢谢！','2016-06-21 23:48:08',0,1,'14.215.59.135',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (298,'李良钦','','13502971816','杏花路旁人行道违章乱搭严重，现在又变成一片废墟，草木已长出车道，给居民出行造成不更，望给整治','2016-06-22 01:34:44',0,1,'183.4.49.164',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (299,'李先生','upimg/2016062203073239685.jpeg,upimg/2016062203074650827.jpeg,upimg/2016062203080122722.jpeg,upimg/2016062203081128267.jpeg,upimg/2016062203082446872.jpeg,upimg/2016062203083450847.jpeg,','18688007577','各位领导，位于金平区三让路一横路，此路段为周围居民，学校学生家长经常进出的主道，路面也有政府设置的公共停车泊位，但是长期以来，给人为的圈为收费停车场，车辆乱停乱放，原本宽敞的路面，变得狭窄，一到高峰时段，周围几所学校放学，家长接送孩子，居民出入，经常堵在这里，造成拥挤。并且还在人行步道盲道上违章搭建停车蓬及收费员生活居住的铁皮屋，实在不雅，也和当前的创文形成反差，肯请有关部门给予清理取缔，还路于民，谢谢','2016-06-22 03:19:00',0,1,'113.84.174.203',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (300,'陈贵平','','15915576080','位于南海路市总工会旁边的南海左旁巷有一个垃圾场，存在己久，望城管部门来解决。','2016-06-22 10:46:49',0,1,'14.215.54.127',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (301,'一片云','','13556363360','杜鹃园后门厕所旁有一处摩托修理铺违章搭建，请有关部门到场查看','2016-06-22 17:57:14',0,1,'183.44.238.149',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (302,'杨先生','upimg/2016062218015295753.jpeg,upimg/2016062218022024606.jpeg,','13822877248',' 违章建筑，建于龙湖沟及龙湖沟绿化带上，存在已有几年，该建筑位于广兴村广寿街，原本好好的路面被占了一半，严重影响车辆通行。希望此次创文可以将该建筑拆除。','2016-06-22 18:04:17',0,1,'111.74.215.81',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (303,'文明城市人人有责','upimg/2016062219092269496.jpg,upimg/2016062219094379147.jpg,upimg/2016062219100178631.jpg,upimg/2016062219101936424.jpg,upimg/2016062219103725935.jpg,','15817947675','龙湖新村健儿幼儿园对面私自占用人行道搭建一处铁皮棚堆放垃圾，从早到晚在路边摆放大量泔水，每天不断运来一车车垃圾倒出来占道挑拣，脏、乱、差。1.造成此处通行的道路常常严重堵塞；2.严重影响环境卫生，滋生细菌蚊虫，恶臭难闻。3.在居民住宅区，中学，龙湖小学，幼儿园旁乱搭建，长年累月堆放垃圾杂物易引起火灾，造成一定的安全隐患；希望相关部门来重视处理，尽快拆除。','2016-06-22 19:10:40',0,1,'183.7.123.86',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (304,'建','','18929621801','创文在我看来是有效果的。要是能细到乡村里更好。应为鸥上这里有好多围建的房子。都是占用池塘和小溪建的。要是能拆掉会空出好多公用地。','2016-06-22 19:30:30',0,1,'112.90.146.246',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (305,'许斌','upimg/2016062221031342673.jpg,','13353083808','路段是金砂路20号，违章占道拆除痛快民心，但是楼梯坎为拆除，还是为完全根除占道','2016-06-22 21:05:59',0,1,'61.164.243.128',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (306,'黄','upimg/2016062221180858556.jpeg,upimg/2016062221181858820.jpeg,upimg/2016062221182676708.jpeg,upimg/2016062221183470185.jpeg,','13924789369','城中村 珠池新村广福街，违章停车场占地20几年，附近及车场后面的住宅楼本来栋距就窄，车场占地后一旦发生火灾根本无法施救。据说该车场有后台，希望相关部门依法办事公平公正，还公共用地于民！','2016-06-22 21:18:43',0,1,'112.90.146.246',NULL,12);
