insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (347,'建','','18929621801','创文很好，要是能细化到乡村应该会更好因为我这里有好多违建，都是占用小溪和池塘违建的。有的人还占用好几平方米。有的人还把占用的地租给别人做工厂还有住宿还有占用公地建房子严重影响我们出行，特别是夏天台风一来或下大雨这积压好多水。希望有关部门能管管。我这里是鸥汀池仔内。','2016-06-26 20:54:52',0,1,'36.111.128.235',NULL,12);
