insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (409,'林先生','','13543950549','经过上一段时间的整治，杏花街:即升侨园市场门口街道有明显好转，但近二天又有乱摆乱卖现象，特别是下午十七点多下班时间，更是突出，街道工作人员视而不见，道路拥堵。让我们下班开车都不好走。请予以取缔，','2016-07-02 23:06:53',0,1,'183.7.97.47',NULL,12);
