insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (435,'文强','','13726533700','汕头市煤气店开在居民楼下隔壁再开饮食店等于炸弹放在火炉边，楼上的居民生命有几分安全，如现在不处理要是出亊，那么出亊是谁的积，煤气店负责起积任吗，','2016-07-05 19:09:29',0,1,'157.61.129.23',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (436,'陈生','upimg/2016070519253698555.jpeg,upimg/2016070519254325985.jpeg,upimg/2016070519254971504.jpeg,','13790867477','作为一名普通市民，在近期看到各级政府部门纷纷开展“还路于民”的创文举措，我要举双手赞成，哪个市民不想要整洁卫生、交通通畅的街区道路环境？日前，我随手拍了几张市区杏花西路因违章搭建而堵塞道路的照片，旁边还有一大片空地，平时沙尘飞扬污染空气，一到下雨就严重积水，给周边居民出行带来许多不便。习总书记讲过：”我们的事业是一点一滴干出来的，我们的道路是一步一个脚印走出来的。“虽然这里地处偏僻，但本人希望政府的创文行动应该一视同仁，只要是违章搭建，只要是影响到市民出行的，都应该重视，借着这次政府施良政的东风，坚决整治、取缔，让更多的市民为汕头政府点赞吧。','2016-07-05 19:25:55',0,1,'14.215.40.204',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (437,'黄静芳','upimg/2016070520121418753.jpeg,upimg/2016070520131899414.jpeg,','13670442181','汕头市龙湖区练江南路5号龙湖工业区E3幢违章搭建屋至今还没拆','2016-07-05 20:17:31',0,1,'113.84.116.62',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (438,'汕头市民','','13411933228','位于龙湖区华美庄北区8栋1楼（金珠小学华美校区）对面家教中心长期存在违章搭建，多次反映为何未见你局工作人员到场处理，是否该网站形同虚设？还是应该再向你局上级政府投诉？','2016-07-05 22:36:26',0,1,'14.158.226.218',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (439,'杨生','upimg/2016070523373230700.jpg,upimg/2016070523381210563.jpg,upimg/2016070523385715539.jpg,','13531260389','位于金平区永祥街道海乾内街（安平路～镇邦路）经前段时间“创文”整治，整条街路观瞻有了明显改善，但地面一些私自砌筑的平台仍然占用一半道路，高低不平，给周边居民及车辆出入造成诸多不便，也存在一些危险，望有关职能部门能够引以重视，尽早进行拆除修复，真正做到还路于民。','2016-07-05 23:39:03',0,1,'14.215.63.245',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (440,'陈小姐','','15876191875','汕头创文之际，作为汕头市民，本应当为了汕头的未来全力支持，然而总有少数胆大包天之人，顶风作案，宁做一粒老鼠屎满足自己的私欲，龙湖区嵩山北路217号金碧花园2栋8楼住户，以公谋私，占用消防通道，私自用铁门封锁，搭建9楼，导致楼下住户房屋多处出现裂痕，住户多次向12345等投诉未果','2016-07-06 09:06:28',0,1,'59.35.67.213',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (441,'郑','','13750404077','在金砂乡内地园1号附近，有人私设杀猪点已久，每过一段时间就在半夜两三点杀猪，严重影响附近居民休息，并且猪的排泻物也没有进行很好的处理，夜间经常臭气熏天，而且杀的猪也不知道是不是安全的生猪，对于这种在居民区私设杀猪点的行为希望有关部门严加取缔。还内地园居民一个舒适的居住环境。','2016-07-06 09:52:01',0,1,'14.215.40.204',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (442,'222','','11111111111','1111','2016-07-06 10:15:10',0,1,'157.122.128.10',NULL,13);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (443,'王先生','upimg/2016070615252851250.jpeg,upimg/2016070615263093787.jpeg,','75481874740','澄海区广益街道美龙路多处违章搭建严重 现在还在陆续搭建中 请及时处理','2016-07-06 15:29:03',0,1,'120.87.243.173',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (444,'余跃浩','','13829615081','夏桂埔桂兴路永辉超市，新佳友超市，二个门口都违建','2016-07-06 17:00:54',0,1,'116.26.100.23',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (445,'林文','upimg/2016070619544256150.jpg,upimg/2016070619545463396.jpg,upimg/2016070619550244024.jpg,upimg/2016070619551023279.jpg,upimg/2016070619551817201.jpg,','13226834866','我市金环南路中环大厦楼下区间道路长期被工商银行，中国移动以及中环大厦物业等三个单位私设铁栏杆等障碍物，长期占用道路用于以上三家单位的车辆停放以及擅自对外收取费用，而且还一直以来有一间违章搭建的房屋设立小卖部.僵尸车等情况，明显与我市创文活动不相符，造成该区间道路长期交通严重堵塞，该区间道路是一条通往连接菜市场的重要道路，希望有关责能部门尽快给予重视清除，还路于民。（附实地拍照）盼回复','2016-07-06 19:56:09',0,1,'42.123.92.124',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (446,'芬','','15013968121','共和市场后门荣福利鱼丸，地上被敲掉之后，又重新建起来。','2016-07-06 21:26:29',0,1,'106.5.196.60',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (447,'佘先生','upimg/2016070622174741239.jpg,','15916614904','海棠14幢楼下，与月季园市场交界对面，占地建设铺面墙体以宣传栏来保护，板顶安装制冷机三部，墙方装排气扇一部沒人来管管，请有关部门来理一理谢塮。','2016-07-06 22:18:29',0,1,'112.90.146.246',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (448,'何俊东','','18023253823','珠池新村珠南28栋跟珠南19栋区间路道，商家围占步道建商铺经营，为何不见拆除？附近群众议论纷纷意见大。','2016-07-07 10:21:00',0,1,'14.158.220.102',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (449,'郑义','upimg/2016070711243082130.jpeg,','13226848243','㔫湖区绿茵庄74幢201，202，204房私自占用公共平台，对平台建筑结构造成安全隐患，以前也曾投诉过，无结果。现创文已经给我们带来希望，望曝光解决，不给创文抹黑，留下死角污点','2016-07-07 11:30:33',0,1,'183.4.61.244',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (450,'陈先生','upimg/2016070718414030574.jpg,','13553390885','外砂镇华新村金董实业西面，原有一条几米宽路被违章搭建，两次火灾消防车都没能靠近，造成严重损失，希望有关部门管一管。','2016-07-07 18:51:48',0,1,'106.5.196.70',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (451,'周丽纯','','13539673529','目前市区创文风盛行，人人叫好。但在做好创文工作同时，城管们能否更温柔一些？早上在春杏路，城管们出来巡逻赶走摊贩，商品能带的都被搬上车没收，搬不走的现场拿着棍子砸了，连椅子都不放过，来势汹汹。连同附近一些守规的商户也遭了殃。门口（门内）的招牌也被砸烂，引起很多人不满！创文风难道只是表面？城管应该带好头，民心才能跟随，这样如同强盗的做法，是难以平息民愤的。希望创文工作能从心出发，而不是靠“抢”“砸”来获得认可！','2016-07-08 07:32:33',0,1,'14.126.183.126',NULL,12);
