insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (470,'姚','','13502750288','在金平区广厦街道荷花社区荷花园17幢楼下104～103房之间公共绿化花圃上，有人破坏绿化，用破旧编织布、废弃木板、家具和布料等易燃物搭建窝棚，长期非法占为己有，窝棚内私拉乱接电线，既破坏绿化，又存在消防隐患。目前正值高温炎热，一旦发生火灾后果不堪想象，希望相关部门尽快予以取缔和消除火灾隐患，还原社区周边居民安全和谐居住环境。','2016-07-10 08:28:28',0,1,'183.46.25.53',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (471,'陈生','','18023254879','红荔园28栋102房，私拆阳台开后门一，占用绿地，还有烧烤招牌，住宅区内，这样谁该管一管呢？','2016-07-10 10:13:14',0,1,'120.236.43.251',NULL,12);
