insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (484,'林先生','','13543950549','你好，在杏花街（升侨园市场门口）现在乱摆乱卖现象又开始了，特别是下午十七点左右，好多下班的车走不了，好乱，请予以清理，谢谢','2016-07-13 10:42:17',0,1,'157.122.148.130',NULL,12);
