insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (569,'伟','','13676136057','位于商平路与泰兴街交界的文明小区街头保安帐逢存火灾隐患，铁门挡路，建议账逢做小，铁门拆除后改为活动铁栏杆。只是建议，请不要泄漏我的姓名，手机号。','2016-07-28 21:14:34',0,1,'116.211.133.12',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (570,'黄生','','13556456715','这修的什么路？才几个月就烂成这样，原来的红色步道砖十几年都比这些完好，这钱都被人贪污了，要查','2016-07-29 10:35:20',0,1,'106.5.196.100',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (571,'詹生','upimg/2016072922100998884.jpg,upimg/2016072922102065796.jpg,upimg/2016072922103146540.jpg,upimg/2016072922104178074.jpg,upimg/2016072922111195836.jpg,','13929623101','当前全市创文行动中，目的是为了进一步加强城市管理，改善市容市貌。但是珠江路两旁的路灯由于年古失修，设备老化，夜晚的路灯非常昏暗，一方面不利于夜间出行，存在安全隐患；另一方面道路昏暗，影响城市整体面貌，希望有关部门实地调研，进行全面修复完善，方便群众出行。','2016-07-29 22:11:14',0,1,'14.215.59.251',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (572,'刘女士','upimg/2016072923574234436.jpg,','13433869465','位于汕头市华坞村大路6号小区内，该图上违章扩搭铁栏杆至今还未拆除，甚至还占道成花圃，周边停摩托车，严重影响道路通行！上个月轰轰烈烈的华坞路拆除违搭物附近居民拍手称快，但巷内的违搭物呢，依然存在，这里是该小区出入的唯一必经之路，里边也搭了停车场，万一发生火灾事故，消防车如何进入，存在非常大的安全隐患！该情况已多番上报但一直无音讯，街道也对此事不加打理，希望有关部门对以上存在的安全隐患予以整顿！','2016-07-30 00:08:24',0,1,'219.131.120.247',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (573,'王小姐','','13650725275','说好的拆除违章建筑，为啥水仙园只拆一半，那些拆除后的还围着花圃，而且到现在都一个月时间了，东西也没运走','2016-07-30 08:29:01',0,1,'106.5.196.55',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (574,'马先生','upimg/2016073019153649940.jpg,','18607540466','广厦街道葵花园多处绿化带被私自改装为停车位，请核查！','2016-07-30 19:16:39',0,1,'112.97.53.154',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (575,'大叔','upimg/2016073019284645873.jpg,upimg/2016073019292422359.jpg,upimg/2016073019294857933.jpg,','13719928387','濠江区海旁路中心市场路段早上中午傍晚占路为市又回潮了！路又堵起来了！是相关部门监管不力还是小摊小贩没有吸收到创文的道理呢？？？','2016-07-30 19:36:21',0,1,'106.5.196.53',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (576,'林端兴','','13553383835','金平区玉井村有非常多违章建筑','2016-07-30 19:41:25',0,1,'14.148.89.200',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (577,'林端奋','','13727656298','金平区玉井村有非常多违章建筑','2016-07-30 19:43:11',0,1,'14.148.89.200',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (578,'黄先生','upimg/2016073101043088629.MOV,upimg/2016073101045828418.MOV,','15089100725','金环南路龙湖沟一侧，创文期间有一段时间有城管人员在那边处置监督的时候，整条路都无人再出来摆摊卖烧烤。现在城管没出来了，一时间烧烤又疯狂出现在龙湖沟一带的街边，整条路乌烟瘴气，严重影响环境和交通安全。希望城管部门要保持监督和监管。维护好我们创文留下来的成就和成绩。','2016-07-31 01:11:02',0,1,'1.180.237.236',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (579,'林生','','13623010953','位于汕头市金平区鮀莲街道占地现象，违章乱摆，水利沟上成建设居民住宅。希望市委市政府高度重视，早日做到还路于民\n','2016-07-31 01:52:48',0,1,'120.52.27.109',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (580,'纪楚强','','15815001313','汕头市东元中二十七巷违章搭建严重，五米大巷剩下一米多','2016-08-01 13:06:36',0,1,'106.5.196.99',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (581,'纪楚强','','15815001313','汕头市东元中二十七巷违章搭建严重','2016-08-01 13:13:31',0,1,'106.5.196.102',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (582,'辛静慧','upimg/2016080114233878686.jpg,','18666052097','龙祥街道泰龙居委，霸占村集体用地，建幼儿园。','2016-08-01 14:25:25',0,1,'218.76.128.85',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (583,'TNT','upimg/2016080217463721052.jpeg,','13413419430','位于外砂镇华新村环乡路。村委会建设的步道、石桌加上绿化是让村民散步乘凉的一个好去处。但是一段时间后，到了晚上一些不自觉的人在这些石桌上吃喝完后，吃剩的食物和酒起就不用收拾。让乡村环境卫生造成很大的影响。希望今后这些不自觉的人能注意。','2016-08-02 18:03:56',0,1,'183.7.107.218',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (584,'郭先生','','13612393482','龙溪路，金墩园，收垃圾的说，做歺饮的每月要向他交50一100元，才会把垃圾收走，合理吗？','2016-08-03 14:00:22',0,1,'106.5.196.102',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (585,'流氓兔','upimg/2016080317005669625.jpg,upimg/2016080317010056060.jpg,upimg/2016080317010750336.jpg,upimg/2016080317011255876.jpg,','13417122911','在汕头澄海的水产市场，早上八点之前，一些小贩直接把摊位摆在路中间，本来就窄的道路，加上买菜的人多，所以每天早上都是严重塞车！！！汕头正在创建文明城市，这样的行为真是自私，秩序也极度混乱，希望有关部门出来管一管！','2016-08-03 17:07:39',0,1,'218.76.128.85',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (586,'郭姐','upimg/2016080319093781672.jpg,','13612414423','玫瑰园46栋楼下修改路面，地下自来水管破损，请尽快修理！','2016-08-03 19:11:16',0,1,'183.46.24.77',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (587,'鼻向','','13046888888','广东省汕头市\n陈厝合安和街金和17巷一处民宅把原有三幢8层楼房拆除后重建为一幢高达十六层楼高的新楼房，然而把原有的一条巷道占为己有，从三楼开始建为楼房,原中间幢楼与后面一处宅基地也留有滴水巷也全占用去建房，明显超高，违规违建，占用公地，难道有钱有势就可以乱搞吗，当地居委难道都由他们说了算的吗，希政府严励打击！\n‌\n‌\n‌','2016-08-04 00:22:13',0,1,'27.41.134.146',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (588,'知足常乐','upimg/2016080419243581416.jpg,','13502935738','广兴村新苗幼儿园在公共场地私设地锁','2016-08-04 19:29:04',0,1,'183.7.85.127',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (589,'姚慧','upimg/2016080422593870056.jpeg,upimg/2016080422595973531.jpeg,upimg/2016080423003877266.jpeg,','13592801070','东夏路与长平路交叉路，往东厦小学方向存在僵尸车。 由于此路口在修整，灰尘较多，导致车辆蒙了一层灰，影响市容，阻碍其他车辆停车。','2016-08-04 23:02:23',0,1,'59.35.232.154',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (590,'颜炜文','','13510633271','广厦新红荔园巿场外道路两旁本来划线停车位就少，商户用各种招数放置东西在停车位内。望有关部门处理。','2016-08-05 15:51:10',0,1,'120.84.9.206',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (591,'阿才','','18929686062','广兴村兴绵街19巷2号、3号楼下杂物成堆，卫生条件太脏，老鼠成群，希望有关部门清理一下。','2016-08-06 10:09:05',0,1,'103.44.207.43',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (592,'段瑞钢','upimg/2016080610180312378.jpg,','13129691678','在杏花桥东北角的堤上，有一处搭建的厂房，由于这处搭建把整条堤路都给切断，现在的路到此处就成了断头路，难道这处不是违章搭建吗?，对面没有断路的搭建都拆了，这处塞了路就不用拆？','2016-08-06 10:25:40',0,1,'59.35.78.208',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (593,'郑生','','18675409282','广厦新城杜鹃园13栋居民楼下一家叫幸运牛的餐厅设备机器整天发出蜂鸣声，給周围居民休息造成不胜其扰，希望有关部门能帮帮居民，要求其整改，給居民一个安宁的休息空间。谢谢！','2016-08-06 18:21:42',0,1,'27.41.131.137',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (594,'郑继龙','','15382937088','每夜乌桥和新桥九点出就出现烧烤摊，夜夜摆，把创文看作儿戏，有关部门也无爱去清理，为什么?希望重视!','2016-08-07 00:08:34',0,1,'183.240.197.138',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (595,'关文','','13536937682','为何华坞路至中山路占道乱搭仍未见效，街道路面崎岖，旧招牌大型影响市安，与创文极不相配，望彻底清理，并修复原貌。','2016-08-07 01:08:57',0,1,'183.46.154.65',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (596,'黄晓琳','upimg/2016080712474040901.jpg,','13226837132','金新路垃圾成堆没有清理，蚊蝇滋生，臭气冲天','2016-08-07 12:47:50',0,1,'183.240.197.143',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (597,'冰淇林','upimg/2016080713080337460.jpg,','18620808603','鸥汀龟桥北路吉贝社区路段，猪脚饭摊档占道经营，广告灯箱乱摆到路面，影响驾驶证视线，阻塞交通','2016-08-07 13:11:12',0,1,'112.96.33.12',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (598,'李生','upimg/2016080718162716748.jpeg,','13592867892','您好，关于之前举报金砂乡薰德里一杂货铺占道经营扰民的事件，相信城管部门已经对其进行过教育批评，但是白天时间尚且还好，一到晚上又陆续摆出铺外，希望城管部门加强夜间巡逻，发现一次取缔一次，共建文明城市','2016-08-07 18:21:06',0,1,'219.128.199.91',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (599,'陈志坚','upimg/2016080718434967274.jpeg,upimg/2016080718442027888.jpeg,','13502951496','东龙路东龙里5号，占地违章，脏乱！','2016-08-07 18:47:25',0,1,'14.215.42.21',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (600,'陈志坚','upimg/2016080718555228767.jpeg,upimg/2016080718561944148.jpeg,','13502951496','东龙路东龙里5号','2016-08-07 18:56:53',0,1,'14.215.42.21',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (601,'陆','upimg/2016080810575165362.jpg,upimg/2016080810580363926.jpg,','15815127421','位于牡丹园8幢3号梯楼下修理钟表存在借店经营已经好几年了，而且把东西放在我们防盗门内，阻碍我们开关门，而且他顾客一些老人老在那坐弄的我们要停车不能生怕不小心碰到人，而且我们住户跟他发生争执不少，借创文能取缔掉。','2016-08-08 11:04:22',0,1,'183.46.31.14',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (602,'蔡诚','','15989734527','百花路芙蓉园六栋楼下夜晚一烧烤摊占道经营，空气污染，噪音扰民，长期滋扰周边群众生活。请取缔。','2016-08-08 23:37:36',0,1,'157.122.148.160',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (603,'王先生','upimg/2016080920341658886.jpg,','13531240248','汕樟立交桥下报亭不是卖报，长期经营雨衣，换摩托车皮坐，经常占道影响交通安全隐患，为汕头创文，请有关部门给与取缔。','2016-08-09 20:43:54',0,1,'120.82.122.249',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (604,'林','','15119984138','桂园4O多栋为什么没有一枝公共路灯？长其占用公地个人收租。','2016-08-10 08:33:09',0,1,'183.240.197.143',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (605,'昆山片云','upimg/2016081018423828360.jpg,upimg/2016081018443535713.jpg,','18665515947','汕头市红荔园28幢101房私改楼梯墙体，破坏楼梯墙体可能带来危险，请有关部门给于处理。','2016-08-10 18:44:46',0,1,'14.158.201.43',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (606,'陈永鸿','upimg/2016081110032781996.jpg,','13502736786','大华街道新兴爱华片区在十年前启动改造不久即没下文至今。在三洁四巷原拆除房屋废墟杂草丛生，一些缺乏公德意认的人便不时把装修废土、生活垃圾倾倒于此，滋生大量蚊虫鼠害，环境日益恶劣，周边居民深受其害，望藉创文活动处理这一问题，还居民一个干净卫生的生活环境。','2016-08-11 10:04:44',0,1,'157.122.148.160',NULL,12);
insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`,`proid`) values (607,'林生','upimg/2016081110081459281.jpeg,upimg/2016081110082379985.jpeg,upimg/2016081110083165936.jpeg,upimg/2016081110083968840.jpeg,','13417097397','近期城管部门针对创文工作对石榴园进行违章建筑拆除工作 进行得非常的成功 但是成功总有绊脚石 刚刚拆完的地方 就开始有部分有私心的居民 对门前进行霸占 建设自己的车位 这位于石榴园34栋以及原垃圾转运站旁边 希望有关部门进行跟进 为创文做到真真正正的成功 感谢你们的工作 辛苦了','2016-08-11 10:10:20',0,1,'14.148.213.195',NULL,12);
